client/src/lib/voiceParser.ts.content:
interface ParsedTransaction {
  amount: string;
  type: "income" | "expense";
  category: string;
  title: string;
  date: string;
}

const categoryKeywords: Record<string, string> = {
  food: "Food",
  groceries: "Food",
  grocery: "Food",
  restaurant: "Food",
  dining: "Food",
  eat: "Food",
  rent: "Rent",
  lease: "Rent",
  bills: "Bills",
  bill: "Bills",
  utilities: "Bills",
  utility: "Bills",
  electricity: "Bills",
  water: "Bills",
  transport: "Transport",
  transportation: "Transport",
  taxi: "Transport",
  uber: "Transport",
  gas: "Transport",
  fuel: "Transport",
  entertainment: "Entertainment",
  movie: "Entertainment",
  game: "Entertainment",
  fun: "Entertainment",
};

const incomeKeywords = ["income", "salary", "received", "earned", "got"];
const expenseKeywords = ["expense", "spent", "paid", "bought", "purchase"];

export function parseVoiceCommand(transcript: string): ParsedTransaction | null {
  const normalized = transcript.toLowerCase().trim();

  const amountMatch = normalized.match(/₹?\s*([0-9,]+(?:\.[0-9]{1,2})?)/);
  if (!amountMatch) {
    return null;
  }

  const amount = amountMatch[1].replace(/,/g, "");

  let type: "income" | "expense" = "expense";

  for (const keyword of expenseKeywords) {
    if (normalized.includes(keyword)) {
      type = "expense";
      break;
    }
  }

  if (type === "expense") {
    for (const keyword of incomeKeywords) {
      if (normalized.includes(keyword)) {
        type = "income";
        break;
      }
    }
  }

  let category = "Other";
  let categoryKeyword = "";
  for (const [keyword, cat] of Object.entries(categoryKeywords)) {
    if (normalized.includes(keyword)) {
      category = cat;
      categoryKeyword = keyword;
      break;
    }
  }

  let title = "";
  const forMatch = normalized.match(/for\s+(.+?)(?:\s+(?:on|yesterday|today|tomorrow)|\s*$)/);
  if (forMatch) {
    title = forMatch[1].trim();
  } else {
    const remainingText = normalized
      .replace(/₹?\s*[0-9,]+(?:\.[0-9]{1,2})?/, "")
      .replace(/\b(add|expense|income|spent|paid|received|earned|bought|purchase|for|the|was|is)\b/g, "")
      .trim();

    if (remainingText) {
      title = remainingText;
    } else if (categoryKeyword) {
      title = categoryKeyword;
    } else {
      title = `${type}`;
    }
  }

  title = title.charAt(0).toUpperCase() + title.slice(1);

  if (!title || title.trim().length === 0) {
    title = category !== "Other" ? category : `${type.charAt(0).toUpperCase() + type.slice(1)}`;
  }

  let date = new Date().toISOString().split("T")[0];
  if (normalized.includes("yesterday")) {
    const yesterday = new Date();
    yesterday.setDate(yesterday.getDate() - 1);
    date = yesterday.toISOString().split("T")[0];
  } else if (normalized.includes("tomorrow")) {
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    date = tomorrow.toISOString().split("T")[0];
  }

  return {
    amount,
    type,
    category,
    title,
    date,
  };
}

client/src/hooks/useVoiceRecognition.ts.content:
import { useState, useEffect, useRef } from "react";

type VoiceStatus = "idle" | "listening" | "processing" | "error";

interface UseVoiceRecognitionReturn {
  isListening: boolean;
  transcript: string;
  status: VoiceStatus;
  error: string | null;
  isSupported: boolean;
  startListening: () => void;
  stopListening: () => void;
  resetTranscript: () => void;
}

export function useVoiceRecognition(): UseVoiceRecognitionReturn {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState("");
  const [status, setStatus] = useState<VoiceStatus>("idle");
  const [error, setError] = useState<string | null>(null);
  const recognitionRef = useRef<any>(null);

  const isSupported =
    typeof window !== "undefined" &&
    ("SpeechRecognition" in window || "webkitSpeechRecognition" in window);

  useEffect(() => {
    if (!isSupported) {
      setError("Speech recognition is not supported in this browser");
      setStatus("error");
      return;
    }

    const SpeechRecognition =
      (window as any).SpeechRecognition ||
      (window as any).webkitSpeechRecognition;

    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = false;
    recognitionRef.current.interimResults = false;
    recognitionRef.current.lang = "en-US";

    recognitionRef.current.onstart = () => {
      setStatus("listening");
      setIsListening(true);
      setError(null);
    };

    recognitionRef.current.onresult = (event: any) => {
      const transcriptResult = event.results[0][0].transcript;
      setTranscript(transcriptResult);
      setStatus("processing");
      setIsListening(false);
    };

    recognitionRef.current.onerror = (event: any) => {
      setError(event.error);
      setStatus("error");
      setIsListening(false);
    };

    recognitionRef.current.onend = () => {
      setIsListening(false);
      if (status === "listening") {
        setStatus("idle");
      }
    };

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    };
  }, [isSupported]);

  const startListening = () => {
    if (!isSupported || !recognitionRef.current) {
      setError("Speech recognition is not supported");
      setStatus("error");
      return;
    }

    try {
      setTranscript("");
      setError(null);
      recognitionRef.current.start();
    } catch (err) {
      setError("Failed to start voice recognition");
      setStatus("error");
    }
  };

  const stopListening = () => {
    if (recognitionRef.current && isListening) {
      recognitionRef.current.stop();
      setIsListening(false);
      setStatus("idle");
    }
  };

  const resetTranscript = () => {
    setTranscript("");
    setStatus("idle");
    setError(null);
  };

  return {
    isListening,
    transcript,
    status,
    error,
    isSupported,
    startListening,
    stopListening,
    resetTranscript,
  };
}

